  <script>
  export default {
    name: 'KoreaMap',
    
    props: {
      activeRegion: {
        type: String,
        default: null
      }
    },
    
    emits: ['region-selected'],
    
    data() {
      return {
        hoveredRegion: null,
        mousePosition: { x: 0, y: 0 },
        // 수정된 regionNames 객체 - SVG path ID와 정확히 일치시킴
        regionNames: {
          seoul: '서울',
          incheon: '인천',
          gyeonggi: '경기도',
          gangwon: '강원도',
          chungbuk: '충청북도',
          chungnam: '충청남도',
          daejeon: '대전',
          jeonbuk: '전라북도',
          gwangju: '광주',
          Jeonnam: '전라남도',
          daegu: '대구',
          Gyeongbuk: '경상북도',
          gyeongnam: '경상남도',
          busan: '부산',
          ulsan: '울산',
          jeju: '제주도',
          north: '북한'
        }
      };
    },
    
    computed: {
      tooltipStyle() {
        // 툴팁 위치 개선 - 마우스 위치보다 약간 위에 표시
        return {
          left: `${this.mousePosition.x}px`,
          top: `${this.mousePosition.y - 30}px` // 마우스 커서보다 위에 표시
        };
      }
    },
    
    mounted() {
      // document 대신 window에 마우스 이벤트 등록
      window.addEventListener('mousemove', this.updateMousePosition);
    },
    
    beforeUnmount() {
      // 컴포넌트 제거 전 이벤트 리스너 제거
      window.removeEventListener('mousemove', this.updateMousePosition);
    },
    
    methods: {
      selectRegion(regionId) {
        this.$emit('region-selected', regionId);
      },
      
      hoverRegion(regionId) {
        this.hoveredRegion = regionId;
      },
      
      resetHover() {
        this.hoveredRegion = null;
      },
      
      getRegionName(regionId) {
        // regionNames에 해당 ID가 있는지 확인하고 이름 반환
        return this.regionNames[regionId] || regionId;
      },
      
      updateMousePosition(event) {
        // 마우스 위치 업데이트
        this.mousePosition = {
          x: event.clientX,
          y: event.clientY
        };
      }
    }
  };
  </script>
  
  <style scoped>
  .korea-map-container {
    width: 100%;
    height: 100%;
